static char *
gldaarch64elf_get_script (int *isfile)
{
  *isfile = 1;

  if (bfd_link_relocatable (&link_info) && config.build_constructors)
    return "ldscripts/aarch64elf.xu";
  else if (bfd_link_relocatable (&link_info))
    return "ldscripts/aarch64elf.xr";
  else if (!config.text_read_only)
    return "ldscripts/aarch64elf.xbn";
  else if (!config.magic_demand_paged)
    return "ldscripts/aarch64elf.xn";
  else if (bfd_link_pie (&link_info)
	   && link_info.combreloc
	   && link_info.relro
	   && (link_info.flags & DF_BIND_NOW))
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xdwe";
      else
	return "ldscripts/aarch64elf.xdw";
    }
  else if (bfd_link_pie (&link_info)
	   && link_info.combreloc)
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xdce";
      else
	return "ldscripts/aarch64elf.xdc";
    }
  else if (bfd_link_pie (&link_info))
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xde";
      else
	return "ldscripts/aarch64elf.xd";
    }
  else if (bfd_link_dll (&link_info) && link_info.combreloc
	   && link_info.relro && (link_info.flags & DF_BIND_NOW))
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xswe";
      else
	return "ldscripts/aarch64elf.xsw";
    }
  else if (bfd_link_dll (&link_info) && link_info.combreloc)
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xsce";
      else
	return "ldscripts/aarch64elf.xsc";
    }
  else if (bfd_link_dll (&link_info))
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xse";
      else
	return "ldscripts/aarch64elf.xs";
    }
  else if (link_info.combreloc && link_info.relro
	   && (link_info.flags & DF_BIND_NOW))
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xwe";
      else
	return "ldscripts/aarch64elf.xw";
    }
  else if (link_info.combreloc)
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xce";
      else
	return "ldscripts/aarch64elf.xc";
    }
  else
    {
      if (link_info.separate_code)
	return "ldscripts/aarch64elf.xe";
      else
	return "ldscripts/aarch64elf.x";
    }
}

 
